<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>	<?php	//get available meta fields	$event_id 				= get_the_ID();	$event_type				= get_post_meta($event_id, 'event_type', true);	$event_date_start = get_post_meta($event_id, 'event_datetime_start', true);	$event_date_end 	= get_post_meta($event_id, 'event_datetime_end', true);	$event_location 	= get_post_meta($event_id, 'event_location', true);	$event_form_id 		= get_post_meta($event_id, 'selected_ninja_form_id', true);	$featured_img 		= wp_get_attachment_image_src( get_post_thumbnail_id( $event_id ), 'single-post-thumbnail' ); 	$slider_images 		= rwmb_meta( 'image_advanced', 'type=image' );	$event_state			= get_event_state($event_id);	?>	<div class="col-md-3 event-meta-wrap <?php echo $event_state ?>-event">		<p class="type"><?php echo $event_type ?></p>		<p class="feat-image"><?php echo  $featured_img[0] ?></p>		<p class="start">Start:<?php echo gmdate('D, d M Y H:i', $event_date_start); ?></p>		<p class="end">End:<?php echo gmdate('D, d M Y H:i', $event_date_end); ?></p>		<p class="now"><?php echo $event_state ?></p>		<p class="location"><?php echo $event_location ?></p>		<p class="ninja-id"><?php echo $event_form_id ?></p>		<p class="slide-img-urls">			<?php			foreach ( $slider_images as $slide ){			echo '<p class="slider-image">'. $slide['full_url'] .'</p>';			}			?>		</p>	</div>	<div class="col-md-6 event-content-wrap">		<header class="entry-header">			<h1 class="entry-title"><?php the_title(); ?></h1>		</header><!-- .entry-header -->		<div class="entry-content">			<?php	the_content(); ?>		</div><!-- .entry-content -->		<footer class="entry-footer">			<?php	 edit_post_link( __( 'Edit', 'openlab-lite' ), '<span class="edit-link">', '</span>' );	 ?>		</footer><!-- .entry-footer -->	</div><!-- col-md-6 --></article><!-- #post-## -->