<?php	$event_id 	= get_the_ID();	$event_type	= get_post_meta($event_id, 'event_type', true);	$permalink = get_post_permalink();?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<header class="entry-header">			<h2 class="entry-title"><a href="<?php echo esc_url($permalink); ?>"><?php the_title(); ?></a></h2>		</header><!-- .entry-header -->		<div class="entry-content <?php if($event_type) : echo esc_attr($event_type); endif;?>">			<?php			$content = get_the_content();			$trimmed = wp_trim_words( $content, $num_words = 40, $more = null );			echo $trimmed;			?>			<div class="buttons <?php if($event_type) : echo esc_attr($event_type); endif;?>">				<a href="<?php echo esc_url($permalink); ?>" class="btn read-more"><?php _e('Διαβαστε περισσοτερα','openlab-txtd') ?></a>			</div>		</div><!-- .entry-content --></article><!-- #post-## -->