<?php$post_id 				= get_the_ID();$featured_img 		= wp_get_attachment_image_src( get_post_thumbnail_id( $post_id ), 'small' );$img_class = 'no-feat-img';if($featured_img[0]){	$img_class = 'has-feat-img';}$formatted_post_date = get_the_date('j M Y');$formatted_post_date = str_replace(" ", "<br>", $formatted_post_date);$post_link = get_post_permalink($post_id);$date_mbl = get_post_time('U', true);?><a href="<?php echo esc_url($post_link) ?>" class="article-link">	<article id="post-<?php the_ID(); ?>" <?php post_class("$img_class"); ?>>	<div class="post-date col-md-2 col-sm-2">			<div class="square-box">			  <div class="square-content-wrap">				<div class="square-content">					<span><?php echo $formatted_post_date; ?></span>				</div>			  </div>			</div>	</div>	<div class="post-image col-md-2 col-sm-2 <?php echo $img_class; ?>">			<div class="square-box">			  <div class="square-content-wrap">				<div class="square-content">					<?php								if($featured_img[0]){									echo '<span class="featured-img" style="background-image: url('. esc_url($featured_img[0]) .');"></span>';								}								else{									echo '<span class="empty-featured-img"></span>';								}				 ?>				</div>			  </div>			</div>	</div>	<div class="post-content-wrap col-md-6 col-sm-6">			<h2 class="entry-title"><?php the_title(); ?></h2>			<?php			if($date_mbl):				$date_mbl = date_i18n('d F Y', $date_mbl);				echo '<div class="date-mobile">'. $date_mbl .'</div>';			endif;			?>			<div class="entry-content">				<?php					$content = get_the_content();					$trimmed = wp_trim_words( $content, $num_words = 15, $more = null );					echo $trimmed;				 ?>			</div><!-- .entry-content -->			<div class="mobile-buttons">				<span><?php echo __('Read More','openlab-txtd') ?></span>			</div>	</div>	<div class="post-link col-md-2 col-sm-2">		<div class="square-box">			  <div class="square-content-wrap">					<div class="square-content">						<?php	echo '<span class="link-img"></span>'; ?>					</div>			  </div>			</div>	</div>	</article><!-- #post-## --></a>