<?php$post_id 					= get_the_ID();$event_date_start = get_post_meta($post_id, 'event_datetime_start', true);$event_date_end 	= get_post_meta($post_id, 'event_datetime_end', true);$event_state			= get_event_state($post_id);$event_type 			= get_post_meta($post_id, 'event_type', true);$s_date 					= gmdate('j.m.Y', $event_date_start);$e_date 					= gmdate('j.m.Y', $event_date_end);$post_link 				= get_post_permalink($post_id);if($s_date == $e_date){	$formatted_post_date = $s_date;}else{	$formatted_post_date = $s_date .' - '. $e_date;}if($event_state && $event_type):	$classes =  $event_state .' '. $event_type;endif;?>	<article id="post-<?php the_ID(); ?>" <?php post_class($classes); ?>>	<div class="post-image col-md-2 col-lg-2 col-xs-2 col-sm-2 <?php if($classes): echo esc_attr($classes); endif; ?>">			<div class="square-box">			  <div class="square-content-wrap">				<div class="square-content">				</div>			  </div>			</div>	</div>	<div class="post-content-wrap col-md-8 col-lg-8 col-xs-8 col-sm-8">			<h2 class="entry-title"><?php the_title(); ?></h2>			<div class="result-date"><?php echo $formatted_post_date; ?></div>			<div class="entry-content">				<?php					$content = get_the_content();					$trimmed = wp_trim_words( $content, $num_words = 15, $more = null );					echo $trimmed;				 ?>			</div><!-- .entry-content -->	</div>	<a href="<?php echo esc_url($post_link) ?>" class="article-link">		<div class="post-link col-md-2 col-lg-2 col-xs-2 col-sm-2">				<?php	echo '<span class="link-img"></span>'; ?>		</div>	</a>	</article><!-- #post-## -->