<?php//How many events we have?$active_events = get_active_events();//We need a total of 3, active events get priority.$active_num = $active_events->post_count;?><section class="focus" id="focus"><div class="container">	<div class="row"><?phpwhile ( $active_events->have_posts() ) : $active_events->the_post();				$event_id = get_the_ID();				$event_type = get_post_meta($event_id, 'event_type', true);				$permalink = get_post_permalink();				$event_date_start = get_post_meta($event_id, 'event_datetime_start', true);				$event_date_end 	= get_post_meta($event_id, 'event_datetime_end', true);				$date_formatted = get_single_event_widget_formatted_date($event_date_start,$event_date_end);				echo '<div class="col-md-3 col-sm-6 active '. esc_attr($event_type) .'">';					echo '<div class="square-box">';						echo '<div class="square-content-wrap">';							echo '<a href="'. esc_url($permalink).'">';								echo '<span class="event-wpap">';									echo '<div class="event-date">'. $date_formatted .'</div>';									echo '<div class="event-img"></div>';									echo '<div class="event-title">'. wp_trim_words( get_the_title( $event_id ), 4, '' ) .'</div>';								echo '</span>';							echo '</a>';						echo '</div>';					echo '</div>';				echo '</div>';endwhile;//Fill the list with past events if neededif( $active_num < 3 ):	$passed_needed_num = 3 - $active_num;	$passed_events = get_passed_events($passed_needed_num);	while ( $passed_events->have_posts() ) : $passed_events->the_post();				$event_id = get_the_ID();				$event_type = get_post_meta($event_id, 'event_type', true);				$permalink = get_post_permalink();				$event_date_start = get_post_meta($event_id, 'event_datetime_start', true);				$event_date_end 	= get_post_meta($event_id, 'event_datetime_end', true);				$date_formatted = get_single_event_widget_formatted_date($event_date_start,$event_date_end);				echo '<div class="col-md-3 col-sm-6 passed '. esc_attr($event_type) .'">';					echo '<div class="square-box">';						echo '<div class="square-content-wrap">';							echo '<a href="'. esc_url($permalink).'">';								echo '<span class="event-wrap">';									echo '<div class="event-date">'. $date_formatted .'</div>';									echo '<div class="event-img"></div>';									echo '<div class="event-title">'. wp_trim_words( get_the_title( $event_id ), 4, '' ) .'</div>';								echo '</span>';							echo '</a>';						echo '</div>';					echo '</div>';				echo '</div>';	endwhile;endif;?>				<!--CALENDAR -->				<div class="col-md-3 col-sm-6 calendar">					<div class="square-box">						<div class="square-content-wrap">								<?php oplb_events_get_calendar(); ?>							</a>						</div>					</div>				</div>	</div></div> <!-- / END CONTAINER --></section>  <!-- / END FOCUS SECTION --><section class="focus-subsection" id="focus-join-us">		<?php		$openlab_sections_bg = get_theme_mod('openlab_bg_home_sections', get_template_directory_uri().'/images/home-bg.jpg');		if($openlab_sections_bg != ""):			echo '<div class="row" style="background-image:url('.esc_url($openlab_sections_bg).');"';		else:			echo '<div class="row">';		endif;		?>		<!-- SECTION TITLE -->		<div class="colored-mask"></div>			<div class="container">				<div class="col-md-6 focus-join-us-content">					<?php					$openlab_ourfocus_title = get_theme_mod('openlab_ourfocus_title',__('Participate in Open Lab','openlab-txtd'));					if( !empty($openlab_ourfocus_title) ):						echo '<h2 class="light-text">'.$openlab_ourfocus_title.'</h2>';					endif;					$openlab_ourfocus_subtitle = get_theme_mod('openlab_ourfocus_subtitle',__('Get in touch, using the participation form.','openlab-txtd'));					if( !empty($openlab_ourfocus_subtitle) ):						echo '<div class="section-legend"><span class="light-text">'.$openlab_ourfocus_subtitle.'</span></div>';					endif;					?>				</div>				<div class="col-md-6 focus-join-us-button">				<?php				$openlab_ourfocus_button_label = get_theme_mod('openlab_ourfocus_button_label',__('Join Us Now!','openlab-txtd'));				$openlab_ourfocus_button_form = get_theme_mod('openlab_ourfocus_button_nf_form');				if( !empty($openlab_ourfocus_button_label) &&	!empty($openlab_ourfocus_button_form) && $openlab_ourfocus_button_form !== 0 ):					echo '<div class="buttons">';							echo '<a href="#" data-toggle="modal" data-target="#home_contact_nf" data-backdrop="static" data-keyboard="true" class="btn join-us-btn open-form-btn">'. strip_tags($openlab_ourfocus_button_label) .'</a>';					echo '</div>';				endif;		 		?>				</div>				<?php				//modal form				if( is_numeric($openlab_ourfocus_button_form) && (defined('NF_PLUGIN_VERSION')) && shortcode_exists( 'ninja_forms' ) ):					echo '<div id="home_contact_nf" class="modal fade nf">';						echo '<div class="button-wrap">';							echo '<button type="button" class="openlab-close-modal" data-dismiss="modal" aria-hidden="true">';								echo '<span class="close-btn-inner"></span>';							echo '</button>';						echo '</div>';						echo '<div class="modal-form-container">';							if( function_exists( 'ninja_forms_display_form' ) ):								ninja_forms_display_form( $openlab_ourfocus_button_form );							endif;						echo '</div>';					echo '</div>';				endif;				?>			</div>	</div></section>